div.script:
    $( document ).ready(function() {
      $.ajax({
        type: "GET",
        url: "../../api/v1/search?q=state%3Apublished%20AND%20category.top:%22sponsors:fonds_tubs%22&rows=2000&fl=id,search_result_link_text,mods.author,mods.title.main,mods.dateIssued&sort=mods.dateIssued+desc&wt=json&json.wrf=?",
        dataType: "jsonp",
        success: function (data) {
          $(data.response.docs).each(function(i, doc){
            var authNames = '';
            var countAuth = doc['mods.author'].length;
            $.each(doc['mods.author'],function(key, authName){
              if (key < 3) {
                authNames = authNames + authName;
                if (key < (countAuth - 1)) {
                  authNames = authNames + ' / ';
                }
              }
              if (key == 3) {
                authNames = authNames + 'et.al.';
              }
            });
            $('#digibib_titleList').append($('
')
              .append(authNames + ': ' + doc['search_result_link_text'] + ''));
          });
        },
      });
    });
